mapper.update:
update bus_statement
set total_amount    = #{totalAmount},
    total_quantity  = #{totalQuantity},
    discount_amount = #{discountAmount}
where id = #{id}

update bus_statement
set status = #{status},
    payee_id = #{payeeId},
    pay_time = #{payTime}
where id = #{id}

update bus_statement
set delete_status = #{deleteStatus}
where id = #{id}
